import React from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  Tooltip,
  LinearProgress
} from '@mui/material';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import LockIcon from '@mui/icons-material/Lock';
import DirectionsRunIcon from '@mui/icons-material/DirectionsRun';
import FavoriteIcon from '@mui/icons-material/Favorite';
import GroupIcon from '@mui/icons-material/Group';
import WhatshotIcon from '@mui/icons-material/Whatshot';
import TimerIcon from '@mui/icons-material/Timer';

const MedalCard = ({ medal, isLocked }) => {
  const getMedalColor = (type) => {
    if (isLocked) return '#808080'; // gray for locked medals

    // 成就勋章颜色
    if (type.includes('AAAA')) return '#FFD700'; // 金色
    if (type.includes('AAA')) return '#FFC125';  // 深金色
    if (type.includes('AA')) return '#EEC900';   // 浅金色
    if (type.includes('A')) return '#CDC673';    // 暗金色
    if (type.includes('BB')) return '#C0C0C0';   // 银色
    if (type.includes('B')) return '#CD7F32';    // 铜色

    // 进步勋章颜色
    if (type.includes('PROGRESS_10')) return '#4169E1'; // 蓝色
    if (type.includes('PROGRESS_5')) return '#1E90FF';  // 浅蓝色
    if (type.includes('PROGRESS_3')) return '#87CEEB';  // 天蓝色

    // 训练勋章颜色
    if (type.includes('TRAINING_60')) return '#9932CC'; // 紫色
    if (type.includes('TRAINING_30')) return '#BA55D3'; // 浅紫色
    if (type.includes('TRAINING_WEEKLY')) return '#DDA0DD'; // 粉紫色

    // 健康勋章颜色
    if (type.includes('HEALTH')) return '#32CD32'; // 绿色
    if (type.includes('ACTIVITY')) return '#98FB98'; // 浅绿色

    // 团队勋章颜色
    if (type.includes('TEAM')) return '#FF69B4'; // 粉色

    return '#FFD700'; // default gold
  };

  const getMedalIcon = (type) => {
    if (isLocked) {
      return <LockIcon style={{ color: '#808080', fontSize: 40 }} />;
    }

    const color = getMedalColor(type);

    // 根据不同类型返回不同图标
    if (type.includes('TRAINING')) {
      return <DirectionsRunIcon style={{ color, fontSize: 40 }} />;
    }
    if (type.includes('HEALTH')) {
      return <FavoriteIcon style={{ color, fontSize: 40 }} />;
    }
    if (type.includes('TEAM')) {
      return <GroupIcon style={{ color, fontSize: 40 }} />;
    }
    if (type.includes('PROGRESS')) {
      return <WhatshotIcon style={{ color, fontSize: 40 }} />;
    }
    if (type.includes('TIME')) {
      return <TimerIcon style={{ color, fontSize: 40 }} />;
    }

    return <EmojiEventsIcon style={{ color, fontSize: 40 }} />;
  };

  const getProgress = () => {
    if (!medal.progress) return null;
    return (
      <Box sx={{ width: '100%', mt: 1 }}>
        <LinearProgress 
          variant="determinate" 
          value={medal.progress} 
          sx={{
            height: 8,
            borderRadius: 5,
            backgroundColor: 'grey.200',
            '& .MuiLinearProgress-bar': {
              backgroundColor: getMedalColor(medal.type)
            }
          }}
        />
        <Typography variant="caption" color="text.secondary" display="block" align="right">
          {medal.progress}%
        </Typography>
      </Box>
    );
  };

  const cardContent = (
    <>
      <Box
        sx={{
          p: 2,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          bgcolor: 'grey.100'
        }}
      >
        {getMedalIcon(medal.type)}
      </Box>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          {medal.name || medal.type.split('_').join(' ')}
        </Typography>
        <Typography variant="body2" color="text.secondary" gutterBottom>
          {isLocked ? medal.requirement : medal.description}
        </Typography>
        {!isLocked && (
          <>
            <Typography variant="caption" display="block" gutterBottom>
              获得时间: {new Date(medal.earnedDate).toLocaleDateString()}
            </Typography>
            {medal.event && (
              <Chip 
                label={medal.event.replace(/_/g, ' ')} 
                size="small" 
                sx={{ mt: 1 }}
              />
            )}
          </>
        )}
        {getProgress()}
      </CardContent>
    </>
  );

  return (
    <Card 
      sx={{ 
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        transition: '0.3s',
        opacity: isLocked ? 0.7 : 1,
        '&:hover': {
          transform: 'scale(1.02)',
          boxShadow: 3
        }
      }}
    >
      {isLocked ? (
        <Tooltip title={`获取条件: ${medal.requirement}`}>
          {cardContent}
        </Tooltip>
      ) : (
        cardContent
      )}
    </Card>
  );
};

export default MedalCard;
