import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Chip,
  CircularProgress
} from '@mui/material';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';

const MedalDisplay = () => {
  const [medals, setMedals] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMedals();
  }, []);

  const fetchMedals = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/medal/medals`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMedals(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching medals:', error);
      setLoading(false);
    }
  };

  const getMedalColor = (type) => {
    if (type.includes('NATIONAL')) return 'gold';
    if (type.includes('REGIONAL')) return '#C0C0C0'; // silver
    if (type.includes('LOCAL')) return '#CD7F32'; // bronze
    return '#FFD700'; // default gold
  };

  const getMedalIcon = (type) => {
    // You can add different medal icons based on type
    return <EmojiEventsIcon style={{ color: getMedalColor(type), fontSize: 40 }} />;
  };

  if (loading) {
    return <CircularProgress />;
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Your Medals
        </Typography>

        <Grid container spacing={3}>
          {medals.map((medal) => (
            <Grid item xs={12} sm={6} md={4} key={medal._id}>
              <Card 
                sx={{ 
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  transition: '0.3s',
                  '&:hover': {
                    transform: 'scale(1.02)',
                    boxShadow: 3
                  }
                }}
              >
                <Box
                  sx={{
                    p: 2,
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    bgcolor: 'grey.100'
                  }}
                >
                  {getMedalIcon(medal.type)}
                </Box>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    {medal.type.split('_').join(' ')}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" gutterBottom>
                    {medal.description}
                  </Typography>
                  <Typography variant="caption" display="block" gutterBottom>
                    Earned on: {new Date(medal.earnedDate).toLocaleDateString()}
                  </Typography>
                  {medal.event && (
                    <Chip 
                      label={medal.event.replace(/_/g, ' ')} 
                      size="small" 
                      sx={{ mt: 1 }}
                    />
                  )}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>

        {medals.length === 0 && (
          <Box sx={{ textAlign: 'center', mt: 4 }}>
            <Typography variant="h6" color="text.secondary">
              No medals earned yet. Keep swimming to earn your first medal!
            </Typography>
          </Box>
        )}
      </Box>
    </Container>
  );
};

export default MedalDisplay;
