import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box
} from '@mui/material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';

const AddPerformanceModal = ({ open, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    time: '',
    date: new Date(),
    competition: '',
    splits: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDateChange = (newDate) => {
    setFormData(prev => ({
      ...prev,
      date: newDate
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    setFormData({
      time: '',
      date: new Date(),
      competition: '',
      splits: []
    });
  };

  const validateTimeFormat = (time) => {
    const timeRegex = /^([0-9]{1,2}:)?[0-5][0-9]\.[0-9]{2}$/;
    return timeRegex.test(time);
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>Add New Performance</DialogTitle>
      <form onSubmit={handleSubmit}>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              name="time"
              label="Time (MM:SS.ms)"
              value={formData.time}
              onChange={handleChange}
              required
              error={formData.time && !validateTimeFormat(formData.time)}
              helperText={formData.time && !validateTimeFormat(formData.time) ? 
                "Format should be MM:SS.ms (e.g., 1:23.45 or 23.45)" : ""}
            />

            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="Date"
                value={formData.date}
                onChange={handleDateChange}
                renderInput={(params) => <TextField {...params} />}
              />
            </LocalizationProvider>

            <TextField
              name="competition"
              label="Competition Name (optional)"
              value={formData.competition}
              onChange={handleChange}
            />

            {/* Split times for longer events */}
            {/* Add more fields as needed */}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose}>Cancel</Button>
          <Button 
            type="submit" 
            variant="contained" 
            color="primary"
            disabled={!validateTimeFormat(formData.time)}
          >
            Add Performance
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
};

export default AddPerformanceModal;
